import "swiper/dist/css/swiper.css";
import axios from '@/plugins/axios';

export default {
    data() {
        return {
            slides: [],
            swiperOption: {
                slidesPerView: 1,
                pagination: {
                    el: '.home__slider-dots',
                    clickable: true
                },
                autoplay: true,
                speed: 1000,
                simulateTouch: false,
                disableOnInteraction: true
            }
        }
    },
    methods: {
      showCallback() {
        this.$store.dispatch("SHOW_CALLBACK_FORM");
      }
    },
    created() {
      axios.get('/slides').then((response) => {
        this.slides = response.data.data
      })
    }
}
